export default {
	methods: {
		getAttrs(arr1, date) {
			let dates = arr1.map(elem => {
				let fillMode = 'solid';
				if (new Date(elem.date).getTime() > date.getTime()) {
					fillMode = 'light';
				}
				return {
					highlight: {
						color: 'product-pink',
						fillMode: fillMode,
					},
					dates: new Date(elem.date),
				};
